create FUNCTION [dbo].[FN_splitValue](@pTarget NVARCHAR(MAX), @pDelim CHAR(1), @pIdxGet SMALLINT, @pFlag NVARCHAR(10) = '')
RETURNS NVARCHAR(255)
AS
BEGIN
    ------------------------------------------------------------------------------
    --    : 
    --  : 2014-08-20
    --  : 
    ------------------------------------------------------------------------------
    --      :     
    --  : 
    --=========================================================================
    -- dbo.FN_splitValue   SELECT dbo.FN_splitValue('2014.8.7', '.', 3, 'D')
    ---------------------------------------------------------------------------
    -- '017-215-5065'   '-'    ,
    ---------------------------------------------------------------------------
    -- dbo.FN_splitValue('[]','[(1)]',[], []("."    2,3     0 ))
    ---------------------------------------------------------------------------
    -- dbo.FN_splitValue('017-215-5065', '-', 1) ------- output -----> '017'
    -- dbo.FN_splitValue('017-215-5065', '-', 2) ------- output -----> '215'
    -- dbo.FN_splitValue('017-215-5065', '-', 3) ------- output -----> '5065'
    --=========================================================================
 
    DECLARE
        @dIdxCurr    SMALLINT,
        @dCutLen     INT,
        @dCut       NVARCHAR(255),
        @dFindSuccess CHAR(1)
 
    SET @dIdxCurr = 1
    SET @dCutLen = charindex(@pDelim, @pTarget)
    SET @dFindSuccess = '0'
 
    IF @dCutLen = 0
        BEGIN
 
            IF @pIdxGet = 1
                SET @dCut = @pTarget
            ELSE
                SET @dCut = ''
 
            SET @dFindSuccess = '1'
 
        END
 
    ELSE
        BEGIN
 
            WHILE @dCutLen <> 0
            BEGIN
 
                SET @dCut = substring(@pTarget, 1, @dCutLen)                        --  
                SET @dCut = ltrim(rtrim(left(@dCut, len(@dCut)-len(@pDelim))))      -- Delimiter 
 
                SET @pTarget = ltrim(rtrim(right(@pTarget, len(@pTarget)-@dCutLen))) --     
 
                IF @dIdxCurr = @pIdxGet
                    BEGIN
                        SET @dFindSuccess = '1'
						
						IF @pFlag = 'D' AND @pIdxGet > 1 --  1()  (, )  1 0 
							IF LEN(@dCut) = 1
								SET @dCut = '0' + @dCut

						BREAK
                    END
 
                SET @dCutLen = charindex(@pDelim, @pTarget)                           --   
                SET @dIdxCurr = @dIdxCurr + 1                                           --  
 
            END
 
        END
 
 
    IF @dFindSuccess = '0'
		BEGIN
			IF @dIdxCurr = @pIdxGet
				IF @pFlag = 'D' AND @pIdxGet > 1 --  1()  (, )  1 0 
					IF LEN(@pTarget) = 1
						SET @dCut = '0' + @pTarget
			ELSE
				SET @dCut = ''
		END

    RETURN @dCut
 
END

GO

CREATE FUNCTION [dbo].[udf_StripHTML]
	(@HTMLText VARCHAR(MAX))
	RETURNS VARCHAR(MAX)
 AS
 BEGIN
	 DECLARE @Start INT;
	 DECLARE @End INT;
	 DECLARE @Length INT;
	 
	 SET @Start = CHARINDEX('<',@HTMLText);
	 SET @End = CHARINDEX('>',@HTMLText,CHARINDEX('<',@HTMLText));
	 SET @Length = (@End - @Start) + 1;
	 WHILE @Start > 0 AND @End > 0 AND @Length > 0
	 BEGIN
		 SET @HTMLText = STUFF(@HTMLText,@Start,@Length,'');
		 SET @Start = CHARINDEX('<',@HTMLText);
		 SET @End = CHARINDEX('>',@HTMLText,CHARINDEX('<',@HTMLText));
		 SET @Length = (@End - @Start) + 1;
	 END
	 
	 RETURN LTRIM(RTRIM(@HTMLText));
 END

