						THEN A.CNowEnd * 1.0 / A.CNowFirst * 100
						ELSE (A.CNowEnd - A.CNowReturn) * 1.0 / A.CNowFirst * 100
				   END
			   , 1)
		   ELSE 0.0
		   END AS CNowLeaveRate, -- 
		   CASE WHEN @P_LEAVE = '1' 
				THEN A.CDetail_Leave_Rate
				ELSE A.CValue
		   END AS CNowAccrueLeaveRate, --  6
		   B.CPastFirst, B.CPastNew, B.CPastReturn, B.CPastEnd, B.CPastLast, --  , , , , 
		   CASE WHEN ISNULL(B.CPastFirst, 0) <> 0 THEN
			   ROUND(
