									 ELSE ( ((B.CPastEnd - B.CPastReturn) * 1.0 / B.CPastFirst * 100) - ((A.CNowEnd - A.CNowReturn) * 1.0 / A.CNowFirst * 100)) / ((B.CPastEnd - B.CPastReturn) * 1.0 / B.CPastFirst * 100) END
							, 2)
