						   ROUND(AVG(CInquire*1.0), 2), ROUND(AVG(CVisit*1.0), 2), ROUND(AVG(CRegist*1.0), 2),
						   ROUND(AVG(CASE WHEN CVisit <> 0 THEN ROUND(CRegist * 1.0 / CVisit, 2) ELSE 0.0 END), 2) AS CRegistRate
