	,SUM(CASE WHEN CRegType = 'Tutoring' AND (CPayMonthCount is null OR CPayMonthCount = 0 OR CPayMonthCount = 1)THEN CPayPrice END) AS '1'
	,SUM(CASE WHEN CRegType = 'Counsel' AND (CPayMonthCount is null OR CPayMonthCount = 0 OR CPayMonthCount = 1) AND CSaleFlagCode = 'COUNSEL' THEN CPayPrice END) AS '()1'
	,SUM(CASE WHEN CRegType = 'Counsel' AND (CPayMonthCount is null OR CPayMonthCount = 0 OR CPayMonthCount = 1) AND CSaleFlagCode = 'INSTITUTE' THEN CPayPrice END) AS '()1'
	,SUM(CASE WHEN CRegType = 'Tutoring' AND (CPayMonthCount is null OR CPayMonthCount = 0 OR CPayMonthCount = 1) AND CSaleFlagCode = 'INSTITUTE' THEN CPayPrice END) AS '()1'

	,SUM(CASE WHEN CRegType = 'Counsel' AND (CPayMonthCount = 2) THEN CPayPrice END) AS '2'
	,SUM(CASE WHEN CRegType = 'Tutoring' AND (CPayMonthCount = 2)THEN CPayPrice END) AS '2'
	,SUM(CASE WHEN CRegType = 'Counsel' AND (CPayMonthCount = 2) AND CSaleFlagCode = 'COUNSEL' THEN CPayPrice END) AS '()2'
	,SUM(CASE WHEN CRegType = 'Counsel' AND (CPayMonthCount = 2) AND CSaleFlagCode = 'INSTITUTE' THEN CPayPrice END) AS '()2'
	,SUM(CASE WHEN CRegType = 'Tutoring' AND (CPayMonthCount = 2) AND CSaleFlagCode = 'INSTITUTE' THEN CPayPrice END) AS '()2'

	,SUM(CASE WHEN CRegType = 'Counsel' AND (CPayMonthCount = 3) THEN CPayPrice END) AS '3'
	,SUM(CASE WHEN CRegType = 'Tutoring' AND (CPayMonthCount = 3)THEN CPayPrice END) AS '3'
	,SUM(CASE WHEN CRegType = 'Counsel' AND (CPayMonthCount = 3) AND CSaleFlagCode = 'COUNSEL' THEN CPayPrice END) AS '()3'
	,SUM(CASE WHEN CRegType = 'Counsel' AND (CPayMonthCount = 3) AND CSaleFlagCode = 'INSTITUTE' THEN CPayPrice END) AS '()3'
	,SUM(CASE WHEN CRegType = 'Tutoring' AND (CPayMonthCount = 3) AND CSaleFlagCode = 'INSTITUTE' THEN CPayPrice END) AS '()3'

	,SUM(CASE WHEN CSaleFlagCode = 'CONSULTING2' THEN CPayPrice END) AS ''
	,SUM(CASE WHEN CSaleFlagCode = 'CONSULTING' THEN CPayPrice END) AS ''	
	,SUM(CASE WHEN CSaleFlagCode = 'SPACERENTAL' THEN CPayPrice END) AS ''		

	,SUM(CASE WHEN (CRegType = 'Counsel' OR CRegType = 'Tutoring') AND CSaleFlagCode = 'COUNSEL' THEN CRtnPrice END) AS ''
	,SUM(CASE WHEN (CRegType = 'Counsel' OR CRegType = 'Tutoring') AND CSaleFlagCode = 'INSTITUTE' THEN CRtnPrice END) AS ''

	,SUM(CASE WHEN CSaleFlagCode = 'CONSULTING2' THEN CRtnPrice END) AS ''
	,SUM(CASE WHEN CSaleFlagCode = 'CONSULTING' THEN CRtnPrice END) AS ''	
	,SUM(CASE WHEN CSaleFlagCode = 'SPACERENTAL' THEN CRtnPrice END) AS ''		

FROM TStudentSaleHistory a (NOLOCK)
	LEFT JOIN TStudent b (NOLOCK) ON b.CCode = a.CCode
	LEFT JOIN TDepartment c (nolock) on c.CDepartCode = a.CDepartCode and c.CDepartStartDate = a.CDepartStartDate
WHERE 
	1 = 1
	AND ((a.CSaleDate >= @startDate AND a.CSaleDate <= @endDate) 
		OR (a.CRtnDate >= @startDate AND a.CRtnDate <= @endDate))
	AND c.CDepartCode like 'EPX%' and c.CParentFacilityCode = 'EPXDIRECT' and c.CEndDate = '2200-12-31'
	-- AND a.CDepartCode = @branchCode
GROUP BY c.CName0, a.CSaleDate 
ORDER BY a.CSaleDate

	return
END

