CREATE PROCEDURE [dbo].[SP_OneCur_My_Lecture_List](

	@staff_id nchar(30) = ''
	)
AS
SELECT 
	a.lecture_idx,
	a.lecture_image,
	a.title,
	(select title from otbl_Mager (NOLOCK) WHERE mager_idx = a.lecture_mager_id) as '',
	(select title from otbl_Category (NOLOCK) WHERE category_idx = a.lecture_category_id) as '',
	(select title from otbl_Category (NOLOCK) WHERE category_idx = a.lecture_sub_category_id) as '',
	(select CName0 from TStaff (NOLOCK) where CStaffId = a.staff_Id) as'',
	(select count(*) from otbl_Lecture_Class (NOLOCK) where lecture_id = a.lecture_idx) as '',
	a.total_credit as '',
	a.limit_student as '',
	(select count(*) from otbl_Lecture_attend_student (NOLOCK) where lecture_id = a.lecture_idx  and del_yn = 'N') as '',
	(select MIN(class_startdatetime) from otbl_Lecture_class (NOLOCK) where lecture_id = a.lecture_idx AND del_yn = 'N') as firstclass_startdatetime,
    (select MAX(class_startdatetime) from otbl_Lecture_class (NOLOCK) where lecture_id = a.lecture_idx AND del_yn = 'N') as lastclass_startdatetime,
	(case when a.temp_save_yn = 'Y' then '' else '' end) as '',
	(case when a.lecture_closed_yn = 'Y' then '2.' else '1.' end) as '',
	--(case when a.lecture_reg_yn = 'Y' then '' else '' end) as ''
	(case 
        when a.lecture_closed_yn = 'Y' THEN '-'
		when a.lecture_reg_yn = 'Y' then '' 
		else '' 
	end) as ''
	
	FROM otbl_Lecture a (NOLOCK)
		LEFT JOIN otbl_Member_profile b (NOLOCK) ON a.main_teacher_id = b.profile_idx
	   left join TDepartNowMember c (NOLOCK) on c.CStaffid = b.staff_id AND c.CMain = 1

	WHERE 1=1
		AND a.del_yn='N'
		--  ,   ,     .
		AND (
			(a.staff_id = @staff_id) 
			OR 
			(SELECT profile_idx from otbl_Member_profile (NOLOCK) where staff_id = @staff_id) in (SELECT teacher_id from otbl_Lecture_attend_teacher(NOLOCK)
								WHERE lecture_id = a.lecture_idx
								AND del_yn = 'N'
								AND a.temp_save_yn = 'N')
			)

