CREATE PROCEDURE [dbo].[SP_TStudent_Count]
	-- Add the parameters for the stored procedure here
	@SEARCHDATE		DATETIME = NULL
AS
BEGIN
	SET NOCOUNT ON;

	--   
	IF ISNULL(@SEARCHDATE, '') = ''
	BEGIN
		SET @SEARCHDATE = CONVERT(VARCHAR(20), GETDATE(), 23)
	END

	--  1  
	IF @SEARCHDATE = CONVERT(DATETIME, LEFT(CONVERT(VARCHAR(20), GETDATE(), 23), 8) + '01')
	BEGIN
		SET @SEARCHDATE = CONVERT(VARCHAR(20), GETDATE(), 23)
	END

	--3    
	--SET @SEARCHDATE = DATEADD(HOUR, -3, @SEARCHDATE)

	--  
	DECLARE @FIRSTDATE		DATETIME
		, @BEFOREMONTH		DATETIME
	SET @FIRSTDATE = CONVERT(DATETIME, LEFT(CONVERT(VARCHAR(20), @SEARCHDATE, 23), 8) + '01')
	SET @BEFOREMONTH = CONVERT(DATETIME, LEFT(CONVERT(VARCHAR(20), DATEADD(MONTH, -1, @SEARCHDATE), 23), 8) + '01')

	--   ,  8 ,     ,   
	IF @SEARCHDATE >= CONVERT(VARCHAR(20), GETDATE(), 23)
		AND (SELECT COUNT(*) FROM TAnalytics_Student_Count(nolock) WHERE CFirstDate = @FIRSTDATE AND CCalcDate = CONVERT(VARCHAR(20), GETDATE(), 23)) = 0
		AND DATEPART(HOUR, GETDATE()) >= 8
	BEGIN
		--
		EXEC dbo.SP_TStudent_Count_Calc @SEARCHDATE
		--   
		IF @SEARCHDATE = CONVERT(VARCHAR(20), GETDATE(), 23)
		BEGIN
			EXEC dbo.SP_TStudent_Count_Calc @BEFOREMONTH
		END
	END

	--    
	IF (SELECT COUNT(*) FROM TAnalytics_Student_Count(nolock) WHERE CFirstDate = @FIRSTDATE) = 0
	BEGIN
		--
		EXEC dbo.SP_TStudent_Count_Calc @SEARCHDATE
	END

	-- 
	select
		Z. as branchName	--
		, Z.[()] + Z.[()] as counselPreCount --[()]
		, Z.[()] as counselNewComer --[()]
		, Z.[()] as counselReturning-- [()]
		, (Z.[()] + Z.[()] - Z.[()] - Z.[()] + Z.[()] + Z.[()]) as counselRetire --[()]
		, Z.[()] + Z.[()] as counselMonthTotal --[()]
		, case when (Z.[()] + Z.[()]) > 0
			then convert(float, (Z.[()] + Z.[()] - Z.[()] - Z.[()] + Z.[()] + Z.[()])) / convert(float, (Z.[()] + Z.[()])) * 100
			else 0 end as counselLeavePercent --[()] /
		, Z.[()] + Z.[()] as tutoringPreCount --[()]
		, Z.[()] as tutoringNewComer --[()]
		, Z.[()] as tutoringReturning --[()]
		, (Z.[()] + Z.[()] - Z.[()] - Z.[()] + Z.[()] + Z.[()]) as tutoringRetire --[()]
		, Z.[()] + Z.[()] as tutoringMonthTotal --[()]
		, case when (Z.[()] + Z.[()]) > 0
			then convert(float, (Z.[()] + Z.[()] - Z.[()] - Z.[()] + Z.[()] + Z.[()])) / convert(float, (Z.[()] + Z.[()])) * 100
			else 0 end as tutoringLeavePercent --[()] /
		, Z.salesMoney + Z.counselTotalMoney + Z.tutoringTotalMoeny as salesMoney --[]
		, Z.counselTotalMoney as counselTotalMoney --[]
		, Z.tutoringTotalMoeny as tutoringTotalMoeny --[]
		, (Z.salesMoney - Z.expenseMoney) as profitMoney --[]
		, Z.CDepartCode as branchCode --[]
	from
		TAnalytics_Student_Count Z(nolock)
	where CFirstDate = @FIRSTDATE
		AND CCalcDate = (select max(CCalcDate) from TAnalytics_Student_Count where CFirstDate = @FIRSTDATE)
	union
	select
		' ' as branchName --
		, SUM(Z.[()] + Z.[()]) as counselPreCount --[()]
		, SUM(Z.[()]) as counselNewComer --[()]
		, SUM(Z.[()]) as counselReturning --[()]
		, SUM(Z.[()] + Z.[()] - Z.[()] - Z.[()] + Z.[()] + Z.[()]) as counselRetire --[()]
		, SUM(Z.[()] + Z.[()]) as counselMonthTotal --[()]
		, case when SUM(Z.[()] + Z.[()]) > 0
			then convert(float, SUM(Z.[()] + Z.[()] - Z.[()] - Z.[()] + Z.[()] + Z.[()])) / convert(float, SUM(Z.[()] + Z.[()])) * 100
			else 0 end as counselLeavePercent --[()] /
		, SUM(Z.[()] + Z.[()]) as tutoringPreCount --[()]
		, SUM(Z.[()]) as tutoringNewComer --[()]
		, SUM(Z.[()]) as tutoringReturning --[()]
		, SUM(Z.[()] + Z.[()] - Z.[()] - Z.[()] + Z.[()] + Z.[()]) as tutoringRetire --[()]
		, SUM(Z.[()] + Z.[()]) as tutoringMonthTotal --[()]
		, case when SUM(Z.[()] + Z.[()]) > 0
			then convert(float, SUM(Z.[()] + Z.[()] - Z.[()] - Z.[()] + Z.[()] + Z.[()])) / convert(float, SUM(Z.[()] + Z.[()])) * 100
			else 0 end as tutoringLeavePercent --[()] /
		, sum(Z.salesMoney + Z.counselTotalMoney + Z.tutoringTotalMoeny) as salesMoney --[]
		, sum(Z.counselTotalMoney) as counselTotalMoney --[]
		, sum(Z.tutoringTotalMoeny) as tutoringTotalMoeny --[]
		, sum(Z.salesMoney - Z.expenseMoney) as profitMoney --[]
		, 'AAA0000' as branchCode --[]
	from
		TAnalytics_Student_Count Z(nolock)
	where CFirstDate = @FIRSTDATE
		AND CCalcDate = (select max(CCalcDate) from TAnalytics_Student_Count where CFirstDate = @FIRSTDATE)
	order by branchCode
END

