	(select CName0 from TStaffLevel (NOLOCK) where CLevelCode = c.CMasterLevelCode) as '',
		(case
			when b.outside_teacher_yn = 'Y' then ''
			else (select COpenName0 from TFacility (NOLOCK) where CDepartCode = c.CDEpartCode)
	end) as '',
	(select count(*) from otbl_Lecture_attend_teacher (NOLOCK) where lecture_id = a.lecture_idx) as '',
	a.total_credit as '',
