	exec SP_EduplexQueryList @Case = 'tHistory'
***********************************************************************************************************************/
History_Handler:
BEGIN
	select a.CBaseDate, a.CFacilityCode, b.COpenName0, a.CStaffId, c.CName0, a.CInDt, a.CType
	  from TTutoringUploadHistory a
	  left join TFacility b on a.CFacilityCode = b.CDepartCode and a.CFacilityStartDate = b.CDepartStartDate
	  left join TStaff c on a.CStaffId = c.CStaffId

	return
END

/***********************************************************************************************************************
	  
	exec SP_EduplexQueryList @Case = 'tEndStudent', @StDt = '2017-09-01', @EdDt = '2018-08-31', @CStudentStatus = 'Retire'
***********************************************************************************************************************/
EndStudent_Handler:
BEGIN
	select 
		case when a.CEndStatus <> '' and a.CTutorEndStatus <> '' then ''
			 when a.CEndStatus <> '' then ''
			 when a.CTutorEndStatus <> '' then ''
		end as ""
		,a.CBranchCode as ""
		,b.COpenName0 as ""
		,a.CCode as ""
		,a.CName as ""
		,a.CSex as ""
		,a.CSchoolCode as ""
		,c.CName as ""
		,case a.CGrade 
			when 'g1' then '1'
			when 'g2' then '2'
			when 'g3' then '3'
			when 'g4' then '4'
			when 'g5' then '5'
			when 'g6' then '6'
			when 'g7' then '1'
			when 'g8' then '2'
			when 'g9' then '3'
			when 'g10' then '1'
			when 'g11' then '2'
			when 'g12' then '3'
			when 'g13' then '()'
			when 'g14' then '()'
			when 'g15' then '()'
			when 'g16' then '()'
		 end as ""
		,a.CEmailStudent as ""
		,(case when a.CPhoneMother IS null then a.CPhoneFather else a.CPhoneMother end) as ""
		,a.CStudentStatus as studentStatus
		,a.CNameFather as ""
		,a.CPhoneFather as ""
