	-- 
	--*************
	INSERT INTO TFacilityDailyCount(CDay, CDepartCode, CDepartStartDate, CStudentCode, CStartDate, CType, CFlag, CGrade)
	SELECT @pDt, 
			a.CStdDepartCode,
			a.CStdDepartStartDate,
			a.CStdCode, 
			@dSTART_DATE,--a.CStartDate,
			'Tutoring', 'End',
			d.CGrade
	  FROM TStudentTutoring a(NOLOCK)
