	) t1
	inner join TStudent t2 (NOLOCK) on t1.CStdCode = t2.CCode and substring(t2.CBranchCode, 1, 3) in ('EPX','ECH')
	group by CStdDepartCode, CStdDepartStartDate, CStdCode, CName, CType

	return
END

