CREATE PROCEDURE [dbo].[SP_WB_DirectSales]
	@case			VARCHAR(30),
	@startDate		NVARCHAR(10) = '',
	@endDate		NVARCHAR(10) = '',
	@branchCode     NVARCHAR(30) = ''
AS
BEGIN
	if @case = 'GetDirectSaleRawData'		goto GetDirectSaleRawData	--  Raw data

	select code='ER', mfg='  (' + @Case + ').'
	return
END


--  Raw data
GetDirectSaleRawData:
BEGIN

SELECT 
	a.CDepartCode as ''
	,c.COpenName0 as ''
	,b.CName as ''
	,CASE 
		WHEN a.CRegType = 'Counsel' THEN ''
		WHEN a.CRegType = 'Tutoring' THEN ''
	END AS ''
	,a.CStartDate as ''
	,a.CEndDate as ''
	,a.CPayCommas AS ''
	,(SELECT CName FROM TCommonCode (NOLOCK) WHERE CCodeGroup = a.CSaleFlagGroupCode AND CCode = a.CSaleFlagCode) AS ''
	,a.CPayMonthCount AS ''
	,(SELECT CName FROM TCommonCode (NOLOCK) WHERE CCodeGroup = a.CPayFlagGroupCode AND CCode = a.CPayFlagCode) AS ''
	,a.CPayNo AS ''
	,a.CPayPrice AS ''
	,a.CSaleDate AS ''
	,a.CSaleMemo AS ''	
	,a.CRtnDate AS '()'
	,a.CRtnPrice AS ''
	,a.CRtnNo AS ''

FROM TStudentSaleHistory a (NOLOCK)
LEFT JOIN TStudent b (NOLOCK) ON b.CCode = a.CCode
LEFT JOIN TFacility c (nolock) on c.CDepartCode = a.CDepartCode and c.CDepartStartDate = a.CDepartStartDate
WHERE 
	1 = 1
	AND ((a.CSaleDate >= @startDate AND a.CSaleDate <= @endDate) 
	OR (a.CRtnDate >= @startDate AND a.CRtnDate <= @endDate))
	and a.CDepartCode = @branchCode


	return
END

