	END

	--------------------------------------------------------------------------------------------------------
	--  
	--------------------------------------------------------------------------------------------------------
	SET @THISDATE = DATEADD(DD, 2, @CWeekFirst)
	SET @TODAYSTART = CONVERT(DATETIME, CONVERT(VARCHAR(10), @THISDATE, 23) + ' ' + @STARTTIME)
	SET @TODAYEND = CONVERT(DATETIME, CONVERT(VARCHAR(10), @THISDATE, 23) + ' ' + @ENDTIME)

