		FROM [dbo].[TStaffDailyAttend]
		WHERE [CStaffId] = @CStaffId AND [CDate] = @BEFOREDAY

		IF @BEFORESTART IS NOT NULL
		BEGIN
			SET @TODAYSTART = CONVERT(DATETIME, CONVERT(VARCHAR(10), @THISDATE, 23) + ' ' + CONVERT(VARCHAR(20), @BEFORESTART, 24))
