		BEGIN
			SET @TODAYEND = CONVERT(DATETIME, CONVERT(VARCHAR(10), @THISDATE, 23) + ' ' + CONVERT(VARCHAR(20), @BEFOREEND, 24))
		END
	END
	
	-- 
