							IF (SELECT COUNT(CDetailNo) 
								 FROM TTradeDetail
								WHERE CRequireDate		= @dCRequireDate
								  AND CTiDepartCode		= @dCTiDepartCode
								  AND CTiDepartStartDate	= @dCTiDepartStartDate
								  AND CTradeNo			= @dCTradeNo
								  AND CtradeStatus		<> 'Complete') = 0
								BEGIN
